        //总体方差
        variance: function(num_arr){
            var sum = 0, count = 0;

            for(var i = 0; i < num_arr.length; i++){
                var number = num_arr[i];

                sum += number;
                count++;
            }

            var avg = sum / count;

            var sum_variance = 0;

            for(var j = 0; j < num_arr.length; j++){
                var number = num_arr[j];
     
                sum_variance += (number - avg) * (number - avg);
            }

            return sum_variance / count;
        },
