        splitTxt: function(txt){
            var compareValue, smallNum, largeNum;
            
            if(txt.indexOf(">=") != -1){
                compareValue = txt.split(">=")[0];
                smallNum = txt.split(">=")[1];

                return [compareValue, smallNum, largeNum];
            }
            else if(txt.indexOf(">") != -1){
                compareValue = txt.split(">")[0];
                smallNum = txt.split(">")[1];

                return [compareValue, smallNum, largeNum];
            }
            else if(txt.indexOf("<=") != -1){
                compareValue = txt.split("<=")[0];
                largeNum = txt.split("<=")[1];

                return [compareValue, smallNum, largeNum];
            }
            else if(txt.indexOf("<") != -1){
                compareValue = txt.split("<")[0];
                largeNum = txt.split("<")[1];

                return [compareValue, smallNum, largeNum];
            }
        },
