    luckysheet.getRowMerge = function(rIndex, c1, c2){
        var r1 = 0, r2 = luckysheet.flowdata.length - 1;

        var str = null;
        if(rIndex > r1){
            for(var r = rIndex; r >= r1; r--){
                for(var c = c1; c <= c2; c++){
                    var cell = luckysheet.flowdata[r][c];
                    
                    if(luckysheet.getObjType(cell) == "object" && ("mc" in cell)){
                        var mc = config["merge"][cell["mc"].r + "_" + cell["mc"].c];

                        if(str == null || mc.r < str){
                            str = mc.r;
                        }
                    }    
                }

                if(luckysheet.rowHasMerge(str - 1, c1, c2) && str > r1){
                    r = str - 1;
                }
                else{
                    break;
                }
            }
        }
        else{
            str = r1;
        }

        var end = null;
        if(rIndex < r2){
            for(var r = rIndex; r <= r2; r++){
                for(var c = c1; c <= c2; c++){
                    var cell = luckysheet.flowdata[r][c];
                    
                    if(luckysheet.getObjType(cell) == "object" && ("mc" in cell)){
                        var mc = config["merge"][cell["mc"].r + "_" + cell["mc"].c];

                        if(end == null || (mc.r + mc.rs - 1) > end){
                            end = mc.r + mc.rs - 1;
                        }
                    }
                }

                if(luckysheet.rowHasMerge(end + 1, c1, c2) && end < r2){
                    r = end + 1;
                }
                else{
                    break;
                }
            }
        }
        else{
            end = r2;
        }

        return [str, end];
    }
