        var jfnqrt = function (x, p) {
            if (x == 0)
                return 0;
            var x0, x1;
            x0 = x;
            x1 = ((p - 1) * x0 / p) + (x / (Math.pow(x0, p - 1) * p));//利用迭代法求解
            while (Math.abs(x1 - x0) > 0.000001) {
                x0 = x1;
                x1 = ((p - 1) * x0 / p) + (x / (Math.pow(x0, p - 1) * p));
            }
            return x1;
        }

