        matchOpt: function(v, d){
            for(var vitem in v){
                if(vitem == "t" && v["t"] in {"drc":1, "arc":1,"sha":1,"shc":1,"shd":1 } ){
                    return false;
                }

                if(vitem=="v"){
                    continue;
                }

                if(!(vitem in d)){
                    return false;
                }

                if(d[vitem] != v[vitem]){
                    return false;
                }
            }

            return true;
        },
        deleteRepeatOpt: function(data, value){
            //var d = $.extend(true, [], data); //原来
            var d = data;
            var _this = this;
            
            if(value instanceof Array){
                for(var i = 0; i < value.length; i++){
                    var vitem = value[i];

                    for(var a = 0; a < d.length; a++){
                        var ditem = data[i]; //var ditem = data[a];?
                        
                        if(_this.matchOpt(vitem, ditem)){
                            delete d[a];
                        }
                    }
                }
            }
            else{
                for(var a = 0; a < d.length; a++){
                    var ditem = d[a];
                    
                    if(_this.matchOpt(value, ditem)){
                        delete d[a];
                    }
                }
            }

            var ret = [];
            for(var i = 0; i < d.length; i++){
                if(d[i] != null){
                    ret.push(d[i]);
                }
            }

            return ret;
        },
