                start_c = item.start_c, 
                end_r = item.end_r, 
                end_c = item.end_c;
            var firstcolumlen = item.firstcolumlen;

            if(luckysheet.flowdata[r] == null){
                continue;
            }
            
            if(luckysheet.flowdata[r][c] == null){ //空单元格
                nullCellRender(r, c, start_r, start_c, end_r, end_c);
            }
